import React from "react";
import { Link } from "react-router-dom";

// Material UI
import theme from "../UI/Theme";
import { makeStyles } from '@mui/styles';
import Grid from '@mui/material/Grid';
import Button from '@mui/material/Button';
import { styled } from '@mui/material/styles';
import TextField from '@mui/material/TextField';

import logo from '../../assets/logo2.jpg';
import facebook from '../../assets/fb.svg';

const useStyles = makeStyles(theme => ({
  footer: {
    backgroundColor: theme.palette.common.darkBrown,
    width: "100%",
    height: "120px",
    zIndex: 1000,
    position: "relative",
    paddingTop: "4%",
    paddingBottom: "8%"
  },
  mainContainer: {
    position: "absolute",
    justifyContent: "space-around"
  },
  gridItem: {
    margin: "3em",

  },
  link: {
    color: theme.palette.common.white,
    fontWeight: 300,
    fontFamily: "Barlow",
    fontSize: "16px",
    textDecoration: "none"
  },
  title: {
    color: theme.palette.common.white,
    fontWeight: 600,
    fontFamily: "Barlow",
    textDecoration: "none"
  }
}))

const OutlinedButton = styled(Button)({
  ...theme.typography.buttonCustomization,
  borderRadius: "10px"
})

const EmailTextField = styled(TextField)({
  '& label.Mui-focused': {
    color: 'white',
  },
  '& .MuiInput-underline:after': {
    borderBottomColor: 'white',
  },
  '& .MuiOutlinedInput-root': {
    '& fieldset': {
      borderColor: 'white',
    },
    '&:hover fieldset': {
      borderColor: 'white',
    },
    '&.Mui-focused fieldset': {
      borderColor: 'white',
    },
  },
});

const Footer = () => {
  const classes = useStyles()

  return (
    <footer className={classes.footer}>
      <Grid container className={classes.mainContainer}>
        <Grid item className={classes.gridItem}>
          <Grid container direction="column" spacing={2}>
            <Grid item>
              Logo
            </Grid>
            <Grid item>
              FINTECHPORT LTD.
            </Grid>
            <Grid item className={classes.link}>
              Lorem ipsum dolor sit amet, consectetur adipiscing.
            </Grid>
            <Grid container>
              <Grid item>
                <img alt="facebook logo" src="facebook" />
              </Grid>
              <Grid item>
                <img alt="linkedin logo" src="linkedin" className={classes.icon} />
              </Grid>
              <Grid item>
                <img alt="twitter logo" src="twitter" className={classes.icon} />
              </Grid>
            </Grid>
          </Grid>
        </Grid>

        <Grid item className={classes.gridItem}>
          <Grid container direction="column" spacing={2}>
            <Grid item className={classes.title}>
              ACCOUNT
            </Grid>
            <Grid component={Link} to="/" item className={classes.link}>
              Sign Up
            </Grid>
            <Grid component={Link} to="/" item className={classes.link}>
              Log In
            </Grid>
            <Grid item component={Link} to="/" className={classes.link}>
              Chat With Us
            </Grid>
          </Grid>
        </Grid>

        <Grid item className={classes.gridItem}>
          <Grid container direction="column" spacing={2}>
            <Grid item className={classes.title}>
              LEARN MORE
            </Grid>
            <Grid item component={Link} to="/" className={classes.link}>
              Esport (WMEGT)
            </Grid>
            <Grid item component={Link} to="/" className={classes.link}>
              Privacy Policy
            </Grid>
          </Grid>
        </Grid>

        <Grid item className={classes.gridItem}>
          <Grid container direction="column" spacing={2}>
            <Grid item className={classes.title}>
              Subscribe to stay updated!
            </Grid>
            <Grid item className={classes.link}>
              <EmailTextField
                label="Email"
                id="outlined-size-small"
                defaultValue=" "
                size="small"
              />
            </Grid>
            <Grid item className={classes.link}>
              <OutlinedButton variant="outlined" color="secondary">Subscribe</OutlinedButton>
            </Grid>
          </Grid>
        </Grid>
      </Grid>
    </footer>
  )
};

export default Footer;
