import React from 'react';

import { Grid } from '@mui/material';
import TextField from '@mui/material/TextField';
import { styled } from '@mui/material/styles';
import theme from "../UI/Theme";
import Button from '@mui/material/Button';

import classes from './ApplicationForm.module.css';

const InputTextField = styled(TextField)({
  '& label.Mui-focused': {
    color: theme.palette.common.lightBlue,
  },
  "&:hover label": {
    color: theme.palette.common.lightBlue
  },
  '& .MuiOutlinedInput-root': {
    '& fieldset': {
      borderColor: 'white',
    },
    '&:hover fieldset': {
      borderColor: theme.palette.common.lightBlue,
    },
    '&.Mui-focused fieldset': {
      borderColor: theme.palette.common.lightBlue,
    },
  }
});

const ContainedButton = styled(Button)({
  ...theme.typography.buttonCustomization,
  color: "white",
  boxShadow: "none",
  width: "24%",
  marginTop: "5%",
  [theme.breakpoints.down("md")]: {
    width: "100%",
    marginRight: "0px",
    marginBottom: "16px"
  }
})

const ApplicationBanner = () => {
  return (
    <div className={classes.form}>
      <h1>Get In Touch</h1>
      <Grid container columnSpacing={6} rowSpacing={4}>
        <Grid item xs={12} md={6}>
          <InputTextField required fullWidth id="filled-required" label="First Name" variant="filled" size="small"/>
        </Grid>
        <Grid item xs={12} md={6}>
          <InputTextField required fullWidth id="filled-required" label="Last Name" variant="filled" size="small"/>
        </Grid>
        <Grid item xs={12} md={6}>
          <InputTextField required fullWidth id="filled-required" label="Email" variant="filled" size="small"/>
        </Grid>
        <Grid item xs={12} md={6}>
          <InputTextField required fullWidth id="filled-required" label="Phone Number" variant="filled" size="small"/>
        </Grid>
        <Grid item xs={12} md={6}>
          <InputTextField required fullWidth id="filled-required" label="Job Title" variant="filled" size="small"/>
        </Grid>
        <Grid item xs={12} md={6}>
          <InputTextField fullWidth id="filled-required" label="Organization" variant="filled" size="small"/>
        </Grid>
        <Grid item xs={12} md={6}>
          <InputTextField required fullWidth id="filled-required" label="Country" variant="filled" size="small"/>
        </Grid>
        <Grid item xs={12} md={12}>
          <InputTextField
            required 
            fullWidth 
            id="filled-textarea"
            label="How do you see the Fintechport Lab helping you?"
            // placeholder="Placeholder"
            multiline
            variant="filled"
            rows={4}
          />
        </Grid>
      </Grid>
      <ContainedButton variant="contained" color="secondary">Submit Application</ContainedButton>
    </div>
  )
};

export default ApplicationBanner;
