import classes from "./Partners.module.css";
import partnersInfo from "./partnersInfo";
import { Grid } from "@mui/material";
import theme from '../UI/Theme';

const partnersList = partnersInfo.map((partner) => {
  const { id, name, logo } = partner;
  return (
    <Grid item xs={4} md={3} key={id}>
      <img src={logo} alt={name} className={classes.logo}/>
    </Grid> 
  )
})

const Partners = () => {
  return (
  <div className={classes["logo-container"]}>
    <Grid item container>
      <Grid item md={3}
      sx={{
        [theme.breakpoints.down("lg")]: {
        marginLeft: "auto",
        marginRight: "auto"
        }
      }}>
      <h2 
        // style={{fontSize: "36px",
        // fontWeight: "700",
        // fontFamily: "Barlow"
        // }}
      >
        Collaboration with <br /> forward-thinking <br /> organizations</h2>
      </Grid>
      <Grid item md={9} container spacing={2}     
        sx={{
        paddingLeft: "84px",
        textAlign: "center",
        [theme.breakpoints.down("md")]: {
          paddingLeft: "7%",
          paddingRight: "7%"
        }
      }}>
        {partnersList}
      </Grid>
    </Grid>
  </div>
  )
};

export default Partners;
