import React from "react";
import { useState, useEffect } from "react";
import { Link as RouterLink} from "react-router-dom";

// Material UI
import theme from "../UI/Theme";
import AppBar from '@mui/material/AppBar';
// import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import Link from '@mui/material/Link';
import { styled } from '@mui/material/styles';
import useScrollTrigger from '@mui/material/useScrollTrigger';
import { Typography } from "@mui/material";
import { makeStyles } from '@mui/styles';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';

import logo from '../../assets/logo2.jpg'

function ElevationScroll(props) {
  const { children } = props;
  const trigger = useScrollTrigger({
    disableHysteresis: true,
    threshold: 0,
  });

  return React.cloneElement(children, {
    elevation: trigger ? 4 : 0,
  });
}

const useStyles = makeStyles(theme => ({
  toolbarMargin: {
    ...theme.mixins.toolbar
  },
  logo: {
    height: "2em",
    paddingLeft: "8px"
  },
  tabContainer: {
    marginLeft: "auto",
    marginRight: "auto"
  },
  tab: {
    fontWeight: 900
  }
}))

const ContainedButton = styled(Button)({
  ...theme.typography.buttonCustomization,
  color: "white",
  boxShadow: "none"
})

const OutlinedButton = styled(Button)({
  ...theme.typography.buttonCustomization
})

const Header = (props) => {
  const classes = useStyles();
  const [value, setValue] = useState("one");
  const handleChange = (event, newValue) => {
    setValue(newValue);
  };




  // Dropdown menu for About Us page
  const [aboutMenuAnchorEl, setAboutMenuAnchorEl] = useState(null);
  const [aboutMenuOpen, setAboutMenuOpen] = useState(false);

  // Selected index
  const [selectedAboutMenuIndex, setSelectedAboutMenuIndex] = useState(null)

  const aboutMenuHandleClick = (event) => {
    setAboutMenuAnchorEl(event.currentTarget);
    setAboutMenuOpen(true);
  };
  const aboutMenuHandleClose = () => {
    setAboutMenuAnchorEl(null)
    setAboutMenuOpen(false);
  };

  const handleAboutUsMenuItemClick = (event, index) => {
    setAboutMenuAnchorEl(null);
    setAboutMenuOpen(false);
    setSelectedAboutMenuIndex(index);
  }

  const aboutMenuOptions = [
    {name: "Fintechport - FinTech Hub of SZ", link: "/about-us/fintech-hub-of-sz"}, 
    {name: "Organisation & Managment", link: "/about-us/organisation-managment"},
    {name: "Business Partners", link: "/about-us/partners"},
    {name: "Photo Gallery", link: "/about-us/gallery"},
    {name: "Career Opportunities", link: "/about-us/open-positions"}    
]

  // Dropdown menu for What's New page
  const [whatIsNewAnchorEl, setWhatIsNewAnchorEl] = useState(null);
  const [whatIsNewOpen, setWhatIsNewOpen] = useState(false);
  const whatIsNewHandleClick = (event) => {
    setWhatIsNewAnchorEl(event.currentTarget);
    setWhatIsNewOpen(true);
  };
  const whatIsNewHandleClose = () => {
    setWhatIsNewAnchorEl(null)
    setWhatIsNewOpen(false);
  };


  useEffect(() => {
    if (window.location.pathname === "/" && value !=="one") {
      setValue("one")
    } else if (window.location.pathname === "/innovation-lab" && value !=="two") {
      setValue("two")
    } else if (window.location.pathname === "/submission-form" && value !=="three") {
      setValue("three")
    } else if (window.location.pathname === "/tech-connect" && value !=="four") {
      setValue("four")
    } else if (window.location.pathname === "/tech-connect/events" && value !=="four") {
      setValue("four")
    } else if (window.location.pathname === "/tech-connect/news" && value !=="four") {
      setValue("four")
    } else if (window.location.pathname === "/about-us" && value !=="five") {
      setValue("five")
    } else if (window.location.pathname === "/about-us/fintech-hub-of-sz" && value !=="five") {
      setValue("five")
    } else if (window.location.pathname === "/about-us/organisation-managment" && value !=="five") {
      setValue("five") 
    } else if (window.location.pathname === "/about-us/partners" && value !=="five") {
      setValue("five") 
    } else if (window.location.pathname === "/about-us/gallery" && value !=="five") {
      setValue("five") 
    } else if (window.location.pathname === "/about-us/open-positions" && value !=="five") {
      setValue("five") 
    } else if (window.location.pathname === "/contacts" && value !=="six") {
      setValue("six")
    } 
  }, [value]);

  return (
    <React.Fragment>
      <ElevationScroll>
        {/* <Box sx={{ flexGrow: 1 }}> */}
          <AppBar position="fixed">
            <Toolbar disableGutters={false}>
              <Link component={RouterLink} to="/" underline="none" onClick={() => setValue("one")}>
                <Stack direction="row">
                  <Typography variant="h3" color="black">FINTECHPORT</Typography>
                  <img alt="company logo" src={logo} className={classes.logo} />
                </Stack>
              </Link>
              <Tabs
                value={value}
                onChange={handleChange}
                textColor="secondary"
                indicatorColor="secondary"
                aria-label="secondary tabs example"
                className={classes.tabContainer}
              >
                <Tab 
                  value="one" 
                  label="Home" 
                  className={classes.tab}
                  component={RouterLink}
                  to="/"
                />
                <Tab 
                  value="two" 
                  label="Innovation Lab" 
                  className={classes.tab}
                  component={RouterLink}
                  to="/innovation-lab"
                />
                <Tab 
                  value="three" 
                  label="Submit an Idea" 
                  className={classes.tab}
                  component={RouterLink}
                  to="/submission-form"
                />
                <Tab 
                  aria-owns={whatIsNewAnchorEl ? "what-is-new-menu" : undefined}
                  aria-haspopup={whatIsNewAnchorEl ? "true" : undefined}
                  onMouseOver = {event => whatIsNewHandleClick(event)}
                  value="four" 
                  label="What's New" 
                  className={classes.tab}
                  component={RouterLink}
                  to="/tech-connect"
                />
                <Tab
                  aria-owns={aboutMenuAnchorEl ? "about-us-menu" : undefined}
                  aria-haspopup={aboutMenuAnchorEl ? "true" : undefined}
                  onMouseOver = {event => aboutMenuHandleClick(event)}
                  value="five" 
                  label="About Us" 
                  className={classes.tab}
                  component={RouterLink}
                  to="/about-us"
                />
                <Tab 
                  value="six" 
                  label="Our Contacts" 
                  className={classes.tab}
                  component={RouterLink}
                  to="/contacts"
                />
              </Tabs>
              <Stack spacing={2} direction="row">
                <ContainedButton variant="contained" color="secondary">Log In</ContainedButton>
                <OutlinedButton variant="outlined" color="secondary">Sign Up</OutlinedButton>
              </Stack>

              <Menu 
                id="about-us-menu" 
                anchorEl={aboutMenuAnchorEl} 
                open={aboutMenuOpen} 
                onClose={aboutMenuHandleClose}
                MenuListProps={{onMouseLeave: aboutMenuHandleClose}}
              >
                {aboutMenuOptions.map((option, index) => (
                  <MenuItem
                    key={option}
                    onClick={(event) => {handleAboutUsMenuItemClick(event, index) ; setValue("five") ; aboutMenuHandleClose()}}
                    selected = {index === selectedAboutMenuIndex && value === "five"}
                    component={RouterLink} to={option.link} 
                    sx={{ 
                      color: selectedAboutMenuIndex === index ? `${theme.palette.common.lightBlue}` : `${theme.palette.text.secondary}`, 
                      fontSize: "16px", 
                      fontFamily: "Barlow", 
                      fontWeight: "400",
                      minWidth: "120px"
                    }}
                  >
                    {option.name}
                  </MenuItem>
                ))}
              </Menu>

              <Menu 
                id="what-is-new-menu" 
                anchorEl={whatIsNewAnchorEl} 
                open={whatIsNewOpen} 
                onClose={whatIsNewHandleClose}
                MenuListProps={{onMouseLeave: whatIsNewHandleClose}}
              >
                <MenuItem 
                  onClick={() => {whatIsNewHandleClose() ; setValue("three")}} 
                  component={RouterLink} to="/tech-connect/events" 
                  sx={{ 
                    color: `${theme.palette.text.secondary}`, 
                    fontSize: "16px", 
                    fontFamily: "Barlow", 
                    fontWeight: "400"
                  }}
                >
                  Tech Events
                </MenuItem>
                <MenuItem 
                  onClick={() => {whatIsNewHandleClose() ; setValue("three")}} 
                  component={RouterLink} to="/tech-connect/news"
                  sx={{ 
                    color: `${theme.palette.text.secondary}`, 
                    fontSize: "16px", 
                    fontFamily: "Barlow", 
                    fontWeight: "400"
                  }}
                >
                  Tech News
                </MenuItem>
              </Menu>

            </Toolbar>
          </AppBar>
        {/* </Box> */}
      </ElevationScroll>
      <div className={classes.toolbarMargin}/>
    </React.Fragment>
  )
}

export default Header;
