import React from "react";
import { Link } from "react-router-dom";

// Material UI
import theme from "../UI/Theme";
import { makeStyles } from '@mui/styles';
import Grid from '@mui/material/Grid';
import Button from '@mui/material/Button';
import { styled } from '@mui/material/styles';
import TextField from '@mui/material/TextField';

import FacebookIcon from '@mui/icons-material/Facebook';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import TwitterIcon from '@mui/icons-material/Twitter';
import YouTubeIcon from '@mui/icons-material/YouTube';

const useStyles = makeStyles(theme => ({
  footer: {
    backgroundColor: theme.palette.common.darkBrown,
    width: "100%",
    height: "120px",
    zIndex: 1000,
    position: "relative",
    paddingTop: "4%",
    paddingBottom: "8%",
    [theme.breakpoints.down("md")]: {
    height: "408px"
    }
  },
  mainContainer: {
    position: "absolute",
    justifyContent: "left",
    paddingLeft: "6%"
    // [theme.breakpoints.down("md")]: {
    //   justifyContent: "center",
    //   }
  },
  link: {
    color: theme.palette.common.white,
    fontWeight: 300,
    fontFamily: "Barlow",
    fontSize: "16px",
    textDecoration: "none",

    "&:hover": {
      color: theme.palette.common.lightBlue,
      transitionDelay: "0.3s"
    }
  },
  text: {
    color: theme.palette.common.white,
    fontWeight: 300,
    fontFamily: "Barlow",
    fontSize: "16px",
    textDecoration: "none"
  },
  title: {
    color: theme.palette.common.white,
    fontWeight: 600,
    fontFamily: "Barlow",
    textDecoration: "none",
  }
}))

const OutlinedButton = styled(Button)({
  ...theme.typography.buttonCustomization,
  borderRadius: "10px"
})

const EmailTextField = styled(TextField)({
  '& label': {
    color: 'white'
  },
  '& label.Mui-focused': {
    color: theme.palette.common.lightBlue,
  },
  "&:hover label": {
    color: theme.palette.common.lightBlue
  },
  '& .MuiOutlinedInput-root': {
    '& fieldset': {
      borderColor: 'white',
    },
    '&:hover fieldset': {
      borderColor: theme.palette.common.lightBlue,
    },
    '&.Mui-focused fieldset': {
      borderColor: theme.palette.common.lightBlue,
    },
  },
});

const Footer = () => {
  const classes = useStyles();

  return (
    <footer className={classes.footer}>
      <Grid container spacing={2} className={classes.mainContainer}>
        <Grid xs={10} md={4} item className={classes.gridItem} sx={{marginTop: "24px"}}>
          <Grid container direction="column" spacing={2} className={classes.gridItemInternal}>
            {/* <Grid item>
              Logo
            </Grid> */}
            <Grid item className={classes.title}>
              FINTECHPORT LTD.
            </Grid>
            <Grid item className={classes.text} sx={{width: "50%", paddingTop: "8px"}}>
              Lorem ipsum dolor sit amet, consectetur adipiscing.
            </Grid>
            <Grid spacing={2} container>
              <Grid item>
                <LinkedInIcon color="primary" />
              </Grid>
              <Grid item>
                <FacebookIcon color="primary"/>
              </Grid>
              <Grid item>
                <TwitterIcon color="primary"/>
              </Grid>
              <Grid item>
                <YouTubeIcon color="primary"/>
              </Grid>
            </Grid>
          </Grid>
        </Grid>

        <Grid xs={5} md={2} item className={classes.gridItem} sx={{marginTop: "24px"}}>
          <Grid container direction="column" spacing={2} className={classes.gridItemInternal}>
            <Grid item className={classes.title}>
              ACCOUNT
            </Grid>
            <Grid component={Link} to="/" item className={classes.link}>
              Sign Up
            </Grid>
            <Grid component={Link} to="/" item className={classes.link}>
              Log In
            </Grid>
            <Grid item component={Link} to="/" className={classes.link}>
              Chat With Us
            </Grid>
          </Grid>
        </Grid>

        <Grid xs={5} md={2} item className={classes.gridItem} sx={{marginTop: "24px"}}>
          <Grid container direction="column" spacing={2} className={classes.gridItemInternal}>
            <Grid item className={classes.title}>
              LEARN MORE
            </Grid>
            <Grid item component={Link} to="/" className={classes.link}>
              Esport (WMEGT)
            </Grid>
            <Grid item component={Link} to="/" className={classes.link}>
              Privacy Policy
            </Grid>
          </Grid>
        </Grid>

        <Grid xs={10} md={4} item className={classes.gridItem} sx={{marginTop: "24px"}}>
          <Grid container direction="column" spacing={2} className={classes.gridItemInternal}>
            <Grid item className={classes.title}>
              Subscribe to stay updated!
            </Grid>
            <Grid item className={classes.link}>
              <EmailTextField
                label="Email"
                id="outlined-size-small"
                defaultValue=" "
                size="small"
              />
            </Grid>
            <Grid item className={classes.link}>
              <OutlinedButton variant="outlined" color="secondary">Subscribe</OutlinedButton>
            </Grid>
          </Grid>
        </Grid>
      </Grid>
    </footer>
  )
};

export default Footer;
