import React from 'react';
import Grid from '@mui/material/Grid';
import { makeStyles } from '@mui/styles';
import classes from './Services.module.css';
import Wrapper from '../UI/Wrapper';
import services from './servicesInfo';


const useStyles = makeStyles(theme => ({
  animatedGrid: {
    animation: "moveInBottom 1s 1.5s ease-out",
    animationFillMode: "backwards"
  },
  "@keyframes moveInBottom": {
    "0%": {
      opacity: 0,
      transform: "translateY(100px)"
    },
    "100%": {
      opacity: 1,
      transform: "translateY(0px)"
    }
  }
}))


const servicesList = services.map((service) => {
  const classes = useStyles();
  const { id, title, desc, icon} = service;
  return (
    <Grid 
      item 
      xs={12} 
      md={6} 
      lg={4} 
      key={id}
      sx={{
        marginBottom: "4rem"
      }}
      className={classes.animatedGrid}
    >
      <div>{icon}</div>
      <h3>{title}</h3>
      <p>{desc}</p>
    </Grid>
  )
})

const Services = () => {
  return (
    <Wrapper className={classes.services}>
      <h2 className={classes["services__title"]}>We offer a complete <br/> range of services</h2>
      <Grid 
        container 
        columnSpacing={{ xs: 1, sm: 4, md: 12 }}
        sx={{
          textAlign: "center", 
          paddingTop: "5%",
          paddingBottom: "5%"
          }}>
        {servicesList}
      </Grid>
    </Wrapper>
  )
};

export default Services;
