import React from "react";
import { useState, useEffect } from "react";
import { Link as RouterLink} from "react-router-dom";

// Material UI
import theme from "../UI/Theme";
import AppBar from '@mui/material/AppBar';
// import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import Link from '@mui/material/Link';
import { styled } from '@mui/material/styles';
import useScrollTrigger from '@mui/material/useScrollTrigger';
import { Typography } from "@mui/material";
import { makeStyles } from '@mui/styles';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';

import logo from '../../assets/logo2.jpg'

function ElevationScroll(props) {
  const { children } = props;
  const trigger = useScrollTrigger({
    disableHysteresis: true,
    threshold: 0,
  });

  return React.cloneElement(children, {
    elevation: trigger ? 4 : 0,
  });
}

const useStyles = makeStyles(theme => ({
  toolbarMargin: {
    ...theme.mixins.toolbar
  },
  logo: {
    height: "2em",
    paddingLeft: "8px"
  },
  tabContainer: {
    marginLeft: "auto",
    marginRight: "auto"
  },
  tab: {
    fontWeight: 900
  },
  menuItem: {
    color: `${theme.palette.text.secondary}`, 
    fontSize: "16px", 
    fontFamily: "Barlow", 
    fontWeight: "400"
  }
}))

const ContainedButton = styled(Button)({
  ...theme.typography.buttonCustomization,
  color: "white",
  boxShadow: "none"
})

const OutlinedButton = styled(Button)({
  ...theme.typography.buttonCustomization
})

const Header = (props) => {
  const classes = useStyles();
  const [value, setValue] = useState("one");
  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  // Dropdown menu for About Us page
  const [anchorEl, setAnchorEl] = useState(null);
  const [open, setOpen] = useState(false);
  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
    setOpen(true);
  };
  const handleClose = () => {
    setAnchorEl(null)
    setOpen(false);
  };


  useEffect(() => {
    if (window.location.pathname === "/" && value !=="one") {
      setValue("one")
    } else if (window.location.pathname === "/innovation-lab" && value !=="two") {
      setValue("two")
    } else if (window.location.pathname === "/submission-form" && value !=="three") {
      setValue("three")
    } else if (window.location.pathname === "/tech-connect" && value !=="four") {
      setValue("four")
    } else if (window.location.pathname === "/about-us" && value !=="five") {
      setValue("five")
    } else if (window.location.pathname === "/contacts" && value !=="six") {
      setValue("six")
    } 
  }, [value]);

  return (
    <React.Fragment>
      <ElevationScroll>
        {/* <Box sx={{ flexGrow: 1 }}> */}
          <AppBar position="fixed">
            <Toolbar disableGutters={false}>
              <Link component={RouterLink} to="/" underline="none" onClick={() => setValue("one")}>
                <Stack direction="row">
                  <Typography variant="h3" color="black">FINTECHPORT</Typography>
                  <img alt="company logo" src={logo} className={classes.logo} />
                </Stack>
              </Link>
              <Tabs
                value={value}
                onChange={handleChange}
                textColor="secondary"
                indicatorColor="secondary"
                aria-label="secondary tabs example"
                className={classes.tabContainer}
              >
                <Tab 
                  value="one" 
                  label="Home" 
                  className={classes.tab}
                  component={RouterLink}
                  to="/"
                />
                <Tab 
                  value="two" 
                  label="Innovation Lab" 
                  className={classes.tab}
                  component={RouterLink}
                  to="/innovation-lab"
                />
                <Tab 
                  value="three" 
                  label="Submit an Idea" 
                  className={classes.tab}
                  component={RouterLink}
                  to="/submission-form"
                />
                <Tab 
                  value="four" 
                  label="What's New" 
                  className={classes.tab}
                  component={RouterLink}
                  to="/tech-connect"
                />
                <Tab
                  aria-owns={anchorEl ? "about-us-menu" : undefined}
                  aria-haspopup={anchorEl ? "true" : undefined}
                  onMouseOver = {event => handleClick(event)}
                  value="five" 
                  label="About Us" 
                  className={classes.tab}
                  component={RouterLink}
                  to="/about-us"
                />
                <Tab 
                  value="six" 
                  label="Our Contacts" 
                  className={classes.tab}
                  component={RouterLink}
                  to="/contacts"
                />
              </Tabs>
              <Stack spacing={2} direction="row">
                <ContainedButton variant="contained" color="secondary">Log In</ContainedButton>
                <OutlinedButton variant="outlined" color="secondary">Sign Up</OutlinedButton>
              </Stack>
              <Menu 
                id="about-us-menu" 
                anchorEl={anchorEl} 
                open={open} 
                onClose={handleClose}
                MenuListProps={{onMouseLeave: handleClose}}
              >
                <MenuItem 
                  onClick={() => {handleClose() ; setValue("five")}} 
                  component={RouterLink} to="/about-us/fintech-hub-of-sz" 
                  classes={{root: classes.menuItem}}
                  // sx={{ 
                  //   color: `${theme.palette.text.secondary}`, 
                  //   fontSize: "16px", 
                  //   fontFamily: "Barlow", 
                  //   fontWeight: "400"
                  // }}
                >
                  Fintechport - FinTech Hub of SZ
                </MenuItem>
                <MenuItem 
                  onClick={() => {handleClose() ; setValue("five")}} 
                  component={RouterLink} to="/about-us/organisation-managment"
                  sx={{ 
                    color: `${theme.palette.text.secondary}`, 
                    fontSize: "16px", 
                    fontFamily: "Barlow", 
                    fontWeight: "400"
                  }}
                >
                  Organisation & Managment
                </MenuItem>
                <MenuItem 
                  onClick={() => {handleClose() ; setValue("five")}} 
                  component={RouterLink} to="/about-us/partners"
                  sx={{ 
                    color: `${theme.palette.text.secondary}`, 
                    fontSize: "16px", 
                    fontFamily: "Barlow", 
                    fontWeight: "400"
                  }}
                >
                  Business Partners
                </MenuItem>
                <MenuItem 
                  onClick={() => {handleClose() ; setValue("five")}} 
                  component={RouterLink} to="/about-us/gallery"
                  sx={{ 
                    color: `${theme.palette.text.secondary}`, 
                    fontSize: "16px", 
                    fontFamily: "Barlow", 

                    fontWeight: "400"
                  }}
                >
                  Photo Gallery
                </MenuItem>
                <MenuItem 
                  onClick={() => {handleClose() ; setValue("five")}} 
                  component={RouterLink} to="/about-us/open-positions"
                  sx={{ 
                    color: `${theme.palette.text.secondary}`, 
                    fontSize: "16px", 
                    fontFamily: "Barlow", 
                    fontWeight: "400"
                  }}
                >
                  Career Opportunities
                </MenuItem>
              </Menu>
            </Toolbar>
          </AppBar>
        {/* </Box> */}
      </ElevationScroll>
      <div className={classes.toolbarMargin}/>
    </React.Fragment>
  )
}

export default Header;
