import React from "react";
import { Link as RouterLink} from "react-router-dom";

// Material UI
import theme from "../UI/Theme";
import { makeStyles } from '@mui/styles';
import Grid from '@mui/material/Grid';
import Button from '@mui/material/Button';
import { styled } from '@mui/material/styles';
import TextField from '@mui/material/TextField';
import Link from '@mui/material/Link';

import OutlinedButtonEl from '../UI/OutlinedButton';

import FacebookIcon from '@mui/icons-material/Facebook';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import TwitterIcon from '@mui/icons-material/Twitter';
import YouTubeIcon from '@mui/icons-material/YouTube';

const useStyles = makeStyles(theme => ({
  footer: {
    backgroundColor: theme.palette.common.darkBrown,
    width: "100%",
    height: "120px",
    zIndex: 1000,
    position: "relative",
    paddingTop: "4%",
    paddingBottom: "8%",
    [theme.breakpoints.down("md")]: {
    height: "518px"
    }
  },
  mainContainer: {
    position: "absolute",
    justifyContent: "left",
    paddingLeft: "6%"
  },
  link: {
    color: theme.palette.common.white,
    fontWeight: 300,
    fontFamily: "Barlow",
    fontSize: "16px",
    textDecoration: "none",

    "&:hover": {
      color: theme.palette.common.lightBlue,
      transitionDelay: "0.3s"
    }
  },
  text: {
    color: theme.palette.common.white,
    fontWeight: 300,
    fontFamily: "Barlow",
    fontSize: "16px",
    textDecoration: "none"
  },
  title: {
    color: theme.palette.common.white,
    fontWeight: 600,
    fontFamily: "Barlow",
    textDecoration: "none",
  },
  icon: {
    "&:hover": {
      color: theme.palette.common.lightBlue,
      transitionDelay: "0.3s"
    }
  }
}))

// const OutlinedButton = styled(Button)({
//   ...theme.typography.buttonCustomization,
//   borderRadius: "10px",
//   [theme.breakpoints.down("md")]: {
//     width: "110%"
//   }
// })

const EmailTextField = styled(TextField)({
  '& label': {
    color: 'white'
  },
  '& label.Mui-focused': {
    color: theme.palette.common.lightBlue,
  },
  "&:hover label": {
    color: theme.palette.common.lightBlue
  },
  '& .MuiOutlinedInput-root': {
    '& fieldset': {
      borderColor: 'white',
    },
    '&:hover fieldset': {
      borderColor: theme.palette.common.lightBlue,
    },
    '&.Mui-focused fieldset': {
      borderColor: theme.palette.common.lightBlue,
    },
  },
  [theme.breakpoints.down("md")]: {
    width: "110%"
  }
});

const Footer = () => {
  const classes = useStyles();

  return (
    <footer className={classes.footer}>
      <Grid container spacing={2} className={classes.mainContainer}>
        <Grid xs={10} md={4} item className={classes.gridItem} sx={{marginTop: "24px"}}>
          <Grid container direction="column" spacing={2} className={classes.gridItemInternal}>
            {/* <Grid item>
              Logo
            </Grid> */}
            <Grid item className={classes.title}>
              FINTECHPORT LTD.
            </Grid>
            <Grid item className={classes.text} sx={{width: "50%"}}>
              Lorem ipsum dolor sit amet, consectetur adipiscing.
            </Grid>
            <Grid spacing={2} container sx={{paddingTop: "12px", paddingLeft: "16px"}}>
              <Grid item>
                <Link target="_blank" href="http://www.linkedin.com">
                  <LinkedInIcon color="primary" className={classes.icon}/>
                </Link>
              </Grid>
              <Grid item>
                <Link target="_blank" href="http://www.facebook.com">
                  <FacebookIcon color="primary" className={classes.icon}/>
                </Link>
              </Grid>
              <Grid item>
                <Link target="_blank" href="http://www.twitter.com">
                  <TwitterIcon color="primary" className={classes.icon}/>
                </Link>
              </Grid>
              <Grid item>
                <Link target="_blank" href="http://www.youtube.com">
                  <YouTubeIcon color="primary" className={classes.icon}/>
                </Link>
              </Grid>
            </Grid>
          </Grid>
        </Grid>

        <Grid xs={5} md={2} item className={classes.gridItem} sx={{marginTop: "24px"}}>
          <Grid container direction="column" spacing={2} className={classes.gridItemInternal}>
            <Grid item className={classes.title}>
              ACCOUNT
            </Grid>
            <Grid component={RouterLink} to="/" item className={classes.link}>
              Sign Up
            </Grid>
            <Grid component={RouterLink} to="/" item className={classes.link}>
              Log In
            </Grid>
            <Grid item component={RouterLink} to="/" className={classes.link}>
              Chat With Us
            </Grid>
          </Grid>
        </Grid>

        <Grid xs={5} md={2} item className={classes.gridItem} sx={{marginTop: "24px"}}>
          <Grid container direction="column" spacing={2} className={classes.gridItemInternal}>
            <Grid item className={classes.title}>
              LEARN MORE
            </Grid>
            <Grid item component={RouterLink} to="/" className={classes.link}>
              Esport (WMEGT)
            </Grid>
            <Grid item component={RouterLink} to="/privacy-policy" className={classes.link}>
              Privacy Policy
            </Grid>
          </Grid>
        </Grid>

        <Grid xs={10} md={4} item className={classes.gridItem} sx={{marginTop: "24px"}}>
          <Grid container direction="column" spacing={2} className={classes.gridItemInternal}>
            <Grid item className={classes.title}>
              Subscribe to stay updated!
            </Grid>
            <Grid item className={classes.link}>
              <EmailTextField
                label="Email"
                id="outlined-size-small"
                defaultValue=" "
                size="small"
              />
            </Grid>
            <Grid item className={classes.link}>
              <OutlinedButtonEl action={"Subscribe"} />
            </Grid>
          </Grid>
        </Grid>
      </Grid>
    </footer>
  )
};

export default Footer;
