import React, { useMemo } from "react";
import { useState, useEffect } from "react";
import { Link as RouterLink} from "react-router-dom";

// Material UI
import theme from "../UI/Theme";
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import Link from '@mui/material/Link';
import { styled } from '@mui/material/styles';
import useScrollTrigger from '@mui/material/useScrollTrigger';
import { IconButton, Typography } from "@mui/material";
import { makeStyles } from '@mui/styles';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import useMediaQuery from '@mui/material/useMediaQuery';
import { useTheme } from '@mui/material/styles';
import SwipeableDrawer from '@mui/material/SwipeableDrawer';
import MenuIcon from '@mui/icons-material/Menu';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText';

import logo from '../../assets/logo2.jpg'

function ElevationScroll(props) {
  const { children } = props;
  const trigger = useScrollTrigger({
    disableHysteresis: true,
    threshold: 0,
  });

  return React.cloneElement(children, {
    elevation: trigger ? 4 : 0,
  });
}

// Customized style
const useStyles = makeStyles(theme => ({
  toolbarMargin: {
    ...theme.mixins.toolbar
  },
  logo: {
    height: "2em",
    padingLeft: "8px",
  },
  tabContainer: {
    marginLeft: "auto",
    marginRight: "auto"
  },
  tab: {
    fontWeight: 900
  },
  drawerItem: {
    ...theme.typography,
  }
}))

const ContainedButton = styled(Button)({
  ...theme.typography.buttonCustomization,
  color: "white",
  boxShadow: "none"
})

const OutlinedButton = styled(Button)({
  ...theme.typography.buttonCustomization
})

// Header component
const Header = (props) => {
  const theme = useTheme();
  const classes = useStyles();
  const matches = useMediaQuery(theme.breakpoints.down('lg'));

  const iOS = process.browser && /iPad|iPhone|iPod/.test(navigator.userAgent);
  const [openDrawer, setOpenDrawer] = useState(false);

  const [value, setValue] = useState("one");
  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  // Dropdown menu for About Us page
  const [aboutMenuAnchorEl, setAboutMenuAnchorEl] = useState(null);
  const [aboutMenuOpen, setAboutMenuOpen] = useState(false);

  const [selectedAboutMenuIndex, setSelectedAboutMenuIndex] = useState(null)

  const aboutMenuHandleClick = (event) => {
    setAboutMenuAnchorEl(event.currentTarget);
    setAboutMenuOpen(true);
  };
  const aboutMenuHandleClose = () => {
    setAboutMenuAnchorEl(null)
    setAboutMenuOpen(false);
  };

  const handleAboutUsMenuItemClick = (event, index) => {
    setAboutMenuAnchorEl(null);
    setAboutMenuOpen(false);
    setSelectedAboutMenuIndex(index);
  }

  const aboutMenuOptions = useMemo(() => [
    {name: "Fintechport - FinTech Hub of SZ", link: "/about-us/fintech-hub-of-sz", activeIndex: "five", selectedAboutMenuIndex: 0}, 
    {name: "Organisation & Managment", link: "/about-us/organisation-managment", activeIndex: "five", selectedAboutMenuIndex: 1},
    {name: "Business Partners", link: "/about-us/partners", activeIndex: "five", selectedAboutMenuIndex: 2},
    {name: "Photo Gallery", link: "/about-us/gallery", activeIndex: "five", selectedAboutMenuIndex: 3},
    {name: "Career Opportunities", link: "/about-us/open-positions", activeIndex: "five", selectedAboutMenuIndex: 4}    
  ], [])

  // Dropdown menu for What's New page
  const [whatIsNewAnchorEl, setWhatIsNewAnchorEl] = useState(null);
  const [whatIsNewOpen, setWhatIsNewOpen] = useState(false);
  const whatIsNewHandleClick = (event) => {
    setWhatIsNewAnchorEl(event.currentTarget);
    setWhatIsNewOpen(true);
  };
  const whatIsNewHandleClose = () => {
    setWhatIsNewAnchorEl(null)
    setWhatIsNewOpen(false);
  };

  // Routes
  const routes = useMemo(() => [
    {name: "Home", link: "/", activeIndex: "one"}, 
    {name: "Innovation Lab", link: "/innovation-lab", activeIndex: "two"}, 
    {name: "Submit an Idea", link: "/submission-form", activeIndex: "three"}, 
    {name: "What's New", link: "/tech-connect", activeIndex: "four"},
    {name: "About Us", link: "/about-us", activeIndex: "five"},
    {name: "Our Contacts", link: "/contacts", activeIndex: "six"}
  ], []);

  useEffect(() => {
    [...aboutMenuOptions, ...routes].forEach(route => {
      switch (window.location.pathname) {
        case `${route.link}`: 
          if (value !==route.activeIndex) {
            setValue(route.activeIndex)
            if (route.selectedAboutMenuIndex && route.selectedAboutMenuIndex !== selectedAboutMenuIndex){
              setSelectedAboutMenuIndex(route.selectedAboutMenuIndex)
            } else {
              setSelectedAboutMenuIndex(null)
            }
          }
          break;
        default:
          break;
      }
    })
  }, [value, aboutMenuOptions, routes, selectedAboutMenuIndex]);

  const tabs = (
    <React.Fragment>
      <Tabs
        value={value}
        onChange={handleChange}
        textColor="secondary"
        indicatorColor="secondary"
        aria-label="secondary tabs example"
        className={classes.tabContainer}
      >
        <Tab 
          value="one" 
          label="Home" 
          className={classes.tab}
          component={RouterLink}
          to="/"
        />
        <Tab 
          value="two" 
          label="Innovation Lab" 
          className={classes.tab}
          component={RouterLink}
          to="/innovation-lab"
        />
        <Tab 
          value="three" 
          label="Submit an Idea" 
          className={classes.tab}
          component={RouterLink}
          to="/submission-form"
        />
        <Tab 
          // aria-owns={whatIsNewAnchorEl ? "what-is-new-menu" : undefined}
          // aria-haspopup={whatIsNewAnchorEl ? "true" : undefined}
          // onMouseOver = {event => whatIsNewHandleClick(event)}
          value="four" 
          label="What's New" 
          className={classes.tab}
          component={RouterLink}
          to="/tech-connect"
        />
        <Tab
          aria-owns={aboutMenuAnchorEl ? "about-us-menu" : undefined}
          aria-haspopup={aboutMenuAnchorEl ? "true" : undefined}
          onMouseOver = {event => aboutMenuHandleClick(event)}
          value="five" 
          label="About Us" 
          className={classes.tab}
          component={RouterLink}
          to="/about-us"
        />
        <Tab 
          value="six" 
          label="Our Contacts" 
          className={classes.tab}
          component={RouterLink}
          to="/contacts"
        />
      </Tabs>
      <Stack spacing={2} direction="row">
        <ContainedButton variant="contained" color="secondary">Log In</ContainedButton>
        <OutlinedButton variant="outlined" color="secondary">Sign Up</OutlinedButton>
      </Stack>
      <Menu 
        id="about-us-menu" 
        anchorEl={aboutMenuAnchorEl} 
        open={aboutMenuOpen} 
        onClose={aboutMenuHandleClose}
        MenuListProps={{onMouseLeave: aboutMenuHandleClose}}
      >
        {aboutMenuOptions.map((option, index) => (
          <MenuItem
            key={option}
            onClick={(event) => {handleAboutUsMenuItemClick(event, index) ; setValue("five") ; aboutMenuHandleClose()}}
            selected = {index === selectedAboutMenuIndex && value === "five"}
            component={RouterLink} to={option.link} 
            sx={{ 
              color: selectedAboutMenuIndex === index ? `${theme.palette.common.lightBlue}` : `${theme.palette.text.secondary}`, 
              fontSize: "16px", 
              fontFamily: "Barlow", 
              fontWeight: "400",
              minWidth: "120px"
            }}
          >
            {option.name}
          </MenuItem>
        ))}
      </Menu>
      <Menu 
        id="what-is-new-menu" 
        anchorEl={whatIsNewAnchorEl} 
        open={whatIsNewOpen} 
        onClose={whatIsNewHandleClose}
        MenuListProps={{onMouseLeave: whatIsNewHandleClose}}
      >
        <MenuItem 
          onClick={() => {whatIsNewHandleClose() ; setValue("three")}} 
          component={RouterLink} to="/tech-connect/events" 
          sx={{ 
            color: `${theme.palette.text.secondary}`, 
            fontSize: "16px", 
            fontFamily: "Barlow", 
            fontWeight: "400"
          }}
        >
          Tech Events
        </MenuItem>
        <MenuItem 
          onClick={() => {whatIsNewHandleClose() ; setValue("three")}} 
          component={RouterLink} to="/tech-connect/news"
          sx={{ 
            color: `${theme.palette.text.secondary}`, 
            fontSize: "16px", 
            fontFamily: "Barlow", 
            fontWeight: "400"
          }}
        >
          Tech News
        </MenuItem>
      </Menu>
    </React.Fragment>
  )

  const drawer = (
    <React.Fragment>
      <SwipeableDrawer 
        disableBackdropTransition={!iOS} 
        disableDiscovery={iOS} 
        open={openDrawer}
        onClose={() => setOpenDrawer(false)}
        PaperProps={{
          sx: {
            backgroundColor: "#21C7C4",
            color: "white",
            width: "68%",
          }
        }}
      >
        <List>
          <ListItem 
            onClick={() => {setOpenDrawer(false); setValue("one")}} 
            selected={value === "one"}
            button 
            component={RouterLink} to="/"
          >
            <ListItemText 
              className={classes.drawerItem}
            >
              Home
            </ListItemText>
          </ListItem>
          <ListItem 
            onClick={() => {setOpenDrawer(false); setValue("two")}} 
            selected={value === "two"}
            button 
            component={RouterLink} to="/innovation-lab"
          >
            <ListItemText>
                Innovation Lab
              </ListItemText>
          </ListItem>
          <ListItem 
            onClick={() => {setOpenDrawer(false); setValue("three")}} 
            selected={value === "three"}
            button 
            component={RouterLink} to="/submission-form"
          >
            <ListItemText>
              Submit an Idea
            </ListItemText>
          </ListItem>
          <ListItem 
            onClick={() => {setOpenDrawer(false); setValue("four")}} 
            selected={value === "four"}
            button 
            component={RouterLink} to="/tech-connect"
          >
            <ListItemText>
              What's New
            </ListItemText>
          </ListItem>
          <ListItem 
            onClick={() => {setOpenDrawer(false); setValue("five")}} 
            selected={value === "five"}
            button 
            component={RouterLink} to="/about-us"
          >
            <ListItemText>
              About Us
            </ListItemText>
          </ListItem>
          <ListItem 
            onClick={() => {setOpenDrawer(false); setValue("six")}} 
            divider
            button 
            component={RouterLink} to="/contacts"
            selected={value === "six"}
            sx={{padingBottom: "24px"}}
          >
            <ListItemText>
              Our Contacts
            </ListItemText>
          </ListItem>
          <ListItem 
            onClick={() => {setOpenDrawer(false); setValue("one")}} 
            button 
            component={RouterLink} to="/"
          >
            <ListItemText>
              Sign Up
            </ListItemText>
          </ListItem>
          <ListItem 
            onClick={() => {setOpenDrawer(false); setValue("one")}} 
            button 
            component={RouterLink} to="/"
          >
            <ListItemText>
              Log In
            </ListItemText>
          </ListItem>
        </List>
      </SwipeableDrawer>
      <IconButton 
          onClick={() => setOpenDrawer(!openDrawer)}
          disableRipple
          sx={{ marginLeft: "auto" }}
        >
          <MenuIcon sx={{color: "#666666", height: "38px", width: "38px"}}/>
        </IconButton>
    </React.Fragment>
  )

  return (
    <React.Fragment>
      <ElevationScroll>
        {/* <Box sx={{ flexGrow: 1 }}> */}
          <AppBar position="fixed">
            <Toolbar disableGutters={false}>
              <Link component={RouterLink} to="/" underline="none" onClick={() => setValue("one")}>
                <Stack direction="row">
                  <Typography variant="h3" color="black">FINTECHPORT</Typography>
                  <img alt="company logo" src={logo} className={classes.logo} />
                </Stack>
              </Link>
              {matches ? drawer : tabs}
            </Toolbar>
          </AppBar>
        {/* </Box> */}
      </ElevationScroll>
      <div className={classes.toolbarMargin}/>
    </React.Fragment>
  )
}

export default Header;
